import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./style.css";
import { auth } from "../firebase";
import { signInWithEmailAndPassword } from "firebase/auth";

function LoginForm() {
  const [email, setEmail] = useState(null);
  const [password, setPassword] = useState(null);
  const [patientID, setPatientID] = useState(null);
  const [userType, setUserType] = useState(null);

  const [data, setData] = useState("");

  const login = () => {
    try {
      const res = signInWithEmailAndPassword(auth, email, password).then((r) =>
        console.log(res)
      );
      redir();
    } catch (err) {
      console.error(err);
      alert(err.message);
    }
  };

  const redir = () => {
    if (userType === "patient") {
      //setData(userType);
      window.location.href = "/patientDashboard";
    } else if (userType === "doctor") {
      window.location.href = "/doctorDashboard";
    } else {
      window.location.href = "/hospitalDashboard";
    }
  };

  return (
    <div className="form">
      <div className="form-body">
        <div className="email">
          <label className="form__label" for="email">
            Email{" "}
          </label>
          <input
            type="email"
            id="email"
            className="form__input"
            placeholder="Email"
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="password">
          <label className="form__label" for="password">
            Password{" "}
          </label>
          <input
            className="form__input"
            type="password"
            id="password"
            placeholder="Password"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <div className="patientid">
          <label className="form__label" htmlFor="patientid">
            Patient ID{" "}
          </label>
          <input
            className="form__input"
            type="patientid"
            id="patientid"
            placeholder="Patient ID"
            onChange={(e) => setPatientID(e.target.value)}
          />
        </div>
      </div>
      <div className="usertype">
        <div className="row">
          <label
            className="form__label"
            htmlFor="usertype"
            style={{ marginLeft: "15px" }}
          >
            User Type
          </label>
          <div id="group1">
            <input
              type="radio"
              value="patient"
              name="group1"
              onChange={(e) => setUserType(e.target.value)}
            />
            <label>Patient</label>
            <input
              type="radio"
              value="doctor"
              name="group1"
              style={{ marginLeft: "15px" }}
              onChange={(e) => setUserType(e.target.value)}
            />
            <label>Doctor</label>
            <input
              type="radio"
              value="hospital"
              name="group1"
              onChange={(e) => setUserType(e.target.value)}
              style={{ marginLeft: "15px" }}
            />
            <label>Hospital</label>
          </div>
        </div>
      </div>
      <div></div>

      <div className="footer">
          <div className="col-sm-3" />
          <div className=" text-secondary" style={{ marginTop: "50px" }}>
            <button
              onClick={() => login()}
              style={{ marginLeft: "50px" }}
              className="btn btn-primary px-4"
            >
              Login
            </button>
          </div>
        {/* <button onClick={} type="submit" className="btn">
          Login
        </button> */}
      </div>
    </div>
  );
}

export default LoginForm;
